<template>
  <div class="biketag-input" :style="`background-image: url(${backgroundSrc})`">
    <label v-if="props.label" :class="props.type === 'checkbox' ? 'checkbox__label' : ''">
      {{ props.label }}
    </label>
    <input
      v-if="props.type === 'checkbox'"
      :type="props.type"
      :class="`checkbox__input--${$attrs.modelValue ? 'checked' : 'unchecked'}`"
      :checked="$attrs"
      @click="update"
    />
    <b-form-input
      v-else
      :disabled="props.disabled"
      v-bind="$attrs"
      :type="props.type"
      @input="update"
    />
    <span>
      <slot></slot>
    </span>
  </div>
</template>

<script setup name="BikeTagButton">
import { computed } from 'vue'

const props = defineProps({
  type: {
    type: String,
    default: 'text',
  },
  label: {
    type: String,
    default: null,
  },
  variant: {
    type: String,
    default: 'svg',
  },
  disabled: {
    type: Boolean,
    default: false,
  },
})

// data
const emit = defineEmits(['update:modelValue'])

// computed
const backgroundSrc = computed(() => {
  switch (props.variant) {
    case 'checkbox':
      return ''
    default:
      return 'data:image/svg+xml;base64,'
  }
})

// methods
function update(e) {
  emit('update:modelValue', props.type === 'checkbox' ? e.target.checked : e)
}
</script>

